package com.gladiatorxu.ch01;

public class Test02 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("10+5="+(10+5));
		System.out.println("10-5="+(10-5));
		System.out.println("10*5="+(10*5));
		System.out.println("10/3="+(10.0/3));
		System.out.println("10/3="+Math.floor(10.0/3));
		System.out.println("10/4="+Math.round(10.0/4));
		System.out.println("|-10|="+Math.abs(-10));
		System.out.println("3^4="+Math.pow(3, 4));
		System.out.println("120的平方根是"+Math.sqrt(120));
		System.out.println("10/6="+Math.round(10.0/6*10)/10.0);
	}

}
